package strings;

public class Snull {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String s1 = null;
		if(s1 == null) //here we use == operator to check null values 
		{
			System.out.println("The String is null");
		}
		else {
			System.out.println("The String is not null");
		}
	}

}
